package courses.coursera.bioinformatics1.ch1

object ReverseComplement2 extends App {
	def revComp(str: String) = {
		val comps = str.map(ch => ch match {
			case 'A' => 'T'
			case 'T' => 'A'
			case 'C' => 'G'
			case 'G' => 'C'
            case _ => throw new Exception("String contains illegal char")
		})
		comps.reverse
	}
	//val res1 = revComp("")
	val res1 = revComp("GATTACA")
	//val res1 = revComp("")
	println(res1)
}
